import React, {useState} from 'react'
import Login from './Login';
import WorkoutSetup from './WorkoutSetup';
import Ready from './Ready';
import Trainer from './Trainer';

export default function Home() {
        
    const [user, setUser] = useState({})
    const [page, setPage] = useState("login")
    
    const saveUserDetails = (i, fn, g) => {
      setUser({ id: i, fName: fn, gender: g })
    }

    const changePage = (pageName) => {setPage(pageName)}
  
    const switchPage = () => {
      switch(page) {
        case "login":
          return <Login saveUserDetails = {saveUserDetails} changePage={changePage} />
        case "workouts":
          return <WorkoutSetup changePage={changePage} />
          case "ready":
            return <Ready  saveUserDetails = {saveUserDetails} changePage={changePage}/>
        case "trainer":
        return <Trainer/>
      }
    } 
  
    return (
    <div>
        {switchPage()}
    </div>
  )
}
