import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Title from './Title';

export default function Login(props) {

    const [password, setPassword] = useState("")
    const [fName, setFName] = useState("")
    const navigate = useNavigate()
    const isInfoValid = () => fName && password  ? true : false
   

   

    const getFullNameAndPassword = (event) => {
        let fullName = event.target.value
        let pass = event.target.value
        if(pass.length>=8 &&fullName.length >= 4 && isNaN(fullName) ) {
            setFName(fullName)
            setPassword(pass)
            console.log("good")
            navigate('/helppage')
            props.saveUserDetails( fullName , pass )
            // props.addUser(fullName)
        } else {
            setFName('')
            setPassword('')
           console.log("invalid")
        }
    }

  
    // const isPassValid = (p) => p.length >= 8  ? true : false
    // const isFNValid = (fn) => fn.length >= 4 && isNaN(fn) ? true : false


  return (
    <div className='loginbox '>
        <h1 class="headline">Register Here:</h1>
          <input type={'text'} id="user" onChange={getFullNameAndPassword} placeholder={"Enter Your FullName"} /> <br />
          <input type={'password'} id="pass" onChange={getFullNameAndPassword} placeholder={"Enter Your Password"} />
          <div className='incorrectMsg' hidden={isInfoValid()}>At least 4 characters and only letters and password 8 characters</div> <br />
          <Title text = {"Help Me!"}/>
    </div>
  )
}
