import Table from './Table';
export default function TasksPage() {

  const [list, setList] = useState([])

  const getAllTasks =() => {
    fetch('/tasks')
    .then(res => res.json())
    .then(data =>setList(data))
  }

  useEffect(getAllTasks,[])
  return (
    <div>{list.map(item =>{
      return(
        <div> 
         <Table  options={item.tName} defultOpt={"Choose Color"} onChangeFn={getAllTasks}/> />
         {/* <h3>{item.tName}</h3>
         <h3>{item.tDescription}</h3>
         <h3>{item.tAssigee}</h3> */}
        </div>
      )
     })}</div>
  )
}
