import React, {  useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import './App.css';
import Login from './Login';
import HelpPage from './HelpPage';


function App() {
const [user, setUser] = useState({})
const addUser = (user) => setUser(user)

  const saveUserDetails = (fn, p) => {
    setUser({ fName: fn, password: p })
  }

  // {+ user.fName }
  return (
    <div className="App">
    <Router>
         <Routes>
            <Route path='/' element ={<Login addUser={addUser} saveUserDetails = {saveUserDetails} />}/>
            <Route path={'/helppage' + user} element ={<HelpPage user ={user} />}/>
         </Routes> 
      </Router>
    </div>
  );
}

export default App;
