import React, {useState} from 'react'
import Login from './Login';
import WorkoutSetup from './WorkoutSetup';
import Ready from './Ready';
import Trainer from './Trainer';

export default function Home() {
    
    const userInformation = {id: "" ,fName: "" ,gender: ""}
    
    const [user, setUser] = useState(userInformation)
    const [page, setPage] = useState("login")
    
    const saveUserDetails = (n, c, t) => {
        let newUser = { id: n, fName: c, gender: t }
        setUser([...user, newUser])
      }

    const changePage = (pageName) => {setPage(pageName)}
  
    const switchPage = () => {
      switch(page) {
        case "login":
          return <Login changePage={changePage} />
        case "workouts":
          return <WorkoutSetup changePage={changePage} />
          case "ready":
            return <Ready  saveUserDetails = {saveUserDetails} changePage={changePage}/>
        case "trainer":
        return <Trainer/>
      }
    } 
  
    return (
    <div>
        {switchPage()}
    </div>
  )
}
