import {React, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Select from './Select';
import Title from './Title'


export default function Rooms(props) {
    const navigate = useNavigate()
    
    const [err, setErr] = useState('')
    const [roomName, setRoomName] = useState('')
    const [roomColor, setRoomColor] = useState('')
    const [roomType, setRoomType] = useState('')
    
    const devices = ["Air-Conditioner","Lamp","Stereo system","Hot water switch"]
    const colors = ["Red", "Green", "Yelow", "Blue"]
    const type = ["Bedroom","kitchen","Shower Room"]
    
    const getRName = (event) => {
        let rName = event.target.value
        if(rName.length >= 2 && rName.length <= 5) {
            setRoomName(rName)
            setErr("")
        } else{
            setErr("name is between 2-5 charcters")
        }
    }

    const getRColor = (event) => {
        let rColor = event.target.value
        if(rColor == "Choose Color") {
            setRoomColor('')
        } else {
            setRoomColor(rColor)
        }
    }

    const getRType = (event) => {
        let rType = event.target.value
        if(rType == "Choose Device") {
            setRoomType('')
        } else {
            setRoomType(rType)
        } 
    }
    

    const addNewRoom = () => {
        if(err || !roomName) {
            alert("Please check the fileds")
        }else{
            props.saveNewRoom(roomName, roomColor, roomType)
            navigate('/')
        }
        
    }


    return (
    <div>
        <Title text={'Smart House'}/>
        <input type={'text'} onChange={getRName} placeholder={"Enter a room name"} /><div>{err}</div> <br />
        <Select options={colors} defultOpt={"Choose Color"} onChangeFn={getRColor}/> <br />
        <Select options={devices} defultOpt={"Choose Room Type"} onChangeFn={getRType}/><br />
        <button className='roomBtn' onClick={addNewRoom}>Add My Room</button>
    </div>
  )
}
