import React, {useState, useEffect} from 'react'
import Title from './Title'
import WorkoutItem from './WorkoutItem'

export default function TraningManger(props) {

  const startWorkout = ()=> {
    let notDoneWorkout = props.workouts.find(item => item.isDone == false)
    props.thisCurrentWorkout(notDoneWorkout)
    props.changePage('workout')
  }
  

  return (
    <div>
      <Title text = {"Welcome Trainer"}/>
      <button className='workoutButton largeBtn blueBG' onClick={startWorkout}>start</button> <br />
      {props.workouts.map(w => <WorkoutItem thisCurrentWorkout={props.thisCurrentWorkout} changePage ={props.changePage} workout={w} />)} 
    </div>
  )
}
