import React, {useState} from 'react'
// import { useLinkClickHandler } from 'react-router-dom'
import Endgame from './Endgame'
import Title from './Title'

export default function Thegame(props) {

  // add the next hooks - player and computer: deck, cardIndex, score
  const [compDeck, setCompDeck] = useState([])
  const [playerDeck, setPlayerDeck] = useState([])
  const [cardIndex, setCardIndex] = useState(0)
  const [gameScore, setGameScore] = useState({ player: 0, comp: 0})
  const [endGame, setEndGame] = useState("endGame")

  // fucntions - createDecks, nextCard, checkRoundWin, gameWinner
  const createDecks = () => {
    // loop generate 52 cards in array
    let cardArr = [];
    for(let card=1; card<=13; card++) {
      for(let i=0; i<4; i++) {
        cardArr.push(card)
      }
    }
    console.log(cardArr);
    // for(let i=1, j=1, card=1; i<=52; i++, j++) {
      //   cardArr.push(card)
      //   if(j == 4) {
        //     j = 0
        //     card++
        //   }
        // }
        
        let pCards = []
        let cCards = []
        let rnd;
        while(cardArr.length > 0) {
          rnd = Math.floor(Math.random() * cardArr.length)
          pCards.push(cardArr[rnd])
          cardArr.splice(rnd, 1)
          
          rnd = Math.floor(Math.random() * cardArr.length)
          cCards.push(cardArr[rnd])
          cardArr.splice(rnd, 1) 
        }
        
        setCompDeck(cCards)
        setPlayerDeck(pCards)
        // setPlayerScore(cCards(rnd)+1)
        // setCompScore.map(cCards.push(cardArr[rnd]))


        console.log(pCards);
        console.log(cCards);
    
  }
   
  const hitButton = () => {
    if(compDeck.length -1 < cardIndex + 1){ // 25 < 26
      props.changePage("end")
    }
    setCardIndex(cardIndex + 1)
    if(compDeck[cardIndex] < playerDeck[cardIndex]) {
      setGameScore({ player: gameScore.player + 1, comp: gameScore.comp })
    } else if (compDeck[cardIndex] > playerDeck[cardIndex]) {
        setGameScore({comp: gameScore.comp + 1, player: gameScore.player})
    }  
  }

  return (
    <div>
      <Title text={"WAR!"} />
      <div>Round: {cardIndex+1}</div>
      <div>Comp</div>
      <div>{compDeck[cardIndex]}</div>
      <div>{props.player.playerName}</div>
      <div>{playerDeck[cardIndex]}</div>
      
      <button onClick={hitButton}>Hit</button>
      <button onClick={createDecks}>Deck</button> <br /><br />
    
      <div>ScoreBoard:</div>
      <div>     player: {gameScore.player}-VS-{gameScore.comp} :comp     </div>
    </div>
  )
}
