import React, {useState} from 'react'



export default function Car(props) {

  const [pos, setPos] = useState(0)

  const changePos = () => {
    if(pos == props.kinder.imagesPath.length - 1) {
      setPos(0)
    } else {
      setPos(pos+1)
    }
  }

  const addToCart = ()=>{}
  

  return (
    <div >
        <h4>{props.kinder.productName}</h4>
        <div>Price: {props.kinder.price}$</div>
        <div>Description: {props.kinder.description}</div>
        <img className='productBox' src={props.kinder.imagesPath[pos]} onClick={changePos} alt={`nameproduct${props.kinder.productName}`}/>
        <button onClick={addToCart}>+</button>
    </div>
  )
}
