import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';


export default function Login(props) {

    const [password, setPassword] = useState("")
    const [fName, setFName] = useState("")
    const navigate = useNavigate()

   



    const getFullName = (event) => {
        let fullName = event.target.value
        if(fullName.length >= 4 && fullName != isNaN) {
            setFName(fullName)
            console.log("good")
            if(isPassValid(password)) {
                props.saveUserDetails( fName , password )
                navigate('/helppage')
            }
        } else {
            setFName('')
           console.log("invalid")
        }
    }
    const getPassword = (event) => {
        let pass = event.target.value
        if(isPassValid(pass)) {
            setPassword(pass)
        } else {
            setPassword('')
        }
    }
    const isInfoValid = () => fName && password  ? true : false
    const isPassValid = () => password.length >= 8  ? true : false


  return (
    <div className='loginbox '>
        <h1 class="headline">Register Here:</h1>
          <input type={'text'} id="user" onChange={getFullName} placeholder={"Enter Your FullName"} /> <br />
          <input type={'password'} id="pass" onChange={getPassword} placeholder={"Enter Your Password"} />
          <div className='incorrectMsg' hidden={isInfoValid()}>At least 4 characters and only letters and password 8 characters</div> <br />
        <h1>Help Me!!!</h1>
    </div>
  )
}
