import React, {useState} from 'react'
import TaskWindow from './TaskWindow'



export default function Table(props) {

    const [currentTask, setCurrentTask] = useState(undefined)
    const [indexTask, setIndexTask] = useState(undefined)

    const currentTaskWindow = () => {
        if(currentTask && props.histotyPage) {
            return <TaskWindow endTask={props.endTask} index={indexTask} task={currentTask} setCurrentTask={setCurrentTask} />
        }
    }

    const updateCurrentTask = (task, index) => {
        setCurrentTask(task)
        setIndexTask(index)
    }


  return (
    <div>
        <table className='table'>
            {props.list.map((task, index) => {
                return (
                    <tr className='tr' onClick={()=>updateCurrentTask(task, index)} > 
                        <td>{task.tName}</td>
                        <td>{task.tAssigee}</td>
                    </tr>
                )
            })}
        </table>
        {currentTaskWindow()}
    </div>
  )
}
