import { useEffect, useState } from 'react';
import Select from './Select'
import Title from './Title';
export default function Login(props) {

    const [err, setErr] = useState('')
    const [numID, setNumID] = useState("")
    const [gender, setGender] = useState("")
    const genders = ["male","female"]

    const getid = (event) => {
        let id = event.target.value
        if(isNaN(id) && id == 9) {
            setNumID(id)
        } else {
            setNumID('')
        }
    }

    const isIdValid = () => numID ? true : false


    const getGender = (event) => {
        // let Gender = event.target.value
        // if(Gender == "Choose Gender") {
        //     setGender('')
        // } 
    }




  return (
    <div>
        <Title text={"Enter your details"}/>
        <input type={'text'} onChange={getid} placeholder={"Enter your id"} />
        <div disabled={numID ? true : false}>Please enter numbers only up to 9 characters</div> <br />
        <input type={'text'}  placeholder={"Enter your fullname"} /> <br />
        <Select options={genders} defultOpt={"Choose Gender"} onChangeFn={getGender}/> <br />
        <button disabled={numID ? false : true}>Next</button>
    </div>
  )
}
