import React, {  useEffect, useState } from 'react';
import './App.css';
import Login from './Component/Login';



function App() {
  const [list, setList] = useState([])

  const getAllTasks =() => {
    fetch('/tasks')
    .then(res =>res.json())
    .then(data =>setList(data))
  }

  useEffect(getAllTasks,[])



  return (
    <div className="App">
   {list.map(item =>{
    return(
      <div> 

       <h3>{item.tName}</h3>
       <h3>{item.tDescription}</h3>
       <h3>{item.tAssigee}</h3>
      </div>
    )
   })}
    <Login/>
    </div>
  );
}

export default App;
