import React, {  useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import './App.css';
import Login from './Component/Login';
import HelpPage from './Component/HelpPage';


function App() {

const [user, setUser] = useState({})


  const saveUserDetails = (fn) => {
    setUser({ fName: fn})
  }






  return (
    <div className="App">
    <Router>
         <Routes>
            <Route path='/' element ={<Login  saveUserDetails = {saveUserDetails} />}/>
            <Route path={'/helppage/' + user.fName} element ={<HelpPage user ={user} />}/>
         </Routes> 
      </Router>

    </div>
  );
}

export default App;
