import React, {useState} from 'react'
import TaskWindow from './TaskWindow'



export default function Table(props) {

    const [currentTask, setCurrentTask] = useState(undefined)


    const currentTaskWindow = () => {
        if(currentTask) {
            return <TaskWindow task={currentTask} setCurrentTask={setCurrentTask} />
        }
    }


  return (
    <div>
        <table className='table'>
            {props.list.map((task) => {
                return (
                    <tr className='tr' onClick={()=>setCurrentTask(task)} > 
                        <td>{task.tName}</td>
                        <td>{task.tAssigee}</td>
                    </tr>
                )
            })}
        </table>
        {currentTaskWindow()}
    </div>
  )
}
