import React, {  useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import HomePage from './components/HomePage';
import FavoritePage from './components/FavoritePage';
import Header from './components/Header';


function App() {


  const [favoriteLocal, setFavoriteLocal] = useState({})

  const saveFavoriteCity = (c,t) => {
    setFavoriteLocal({city: c, temperature: t})

  }

// autocomplete
// "http://dataservice.accuweather.com/locations/v1/cities/autocomplete?apikey=WebHzrX0S4ZGR1xqMmgg9s1cMgcjL6CE&q=tel"

// currentconditions
// "http://dataservice.accuweather.com/currentconditions/v1/215854?apikey=WebHzrX0S4ZGR1xqMmgg9s1cMgcjL6CE"

// 5 Days
// "http://dataservice.accuweather.com/forecasts/v1/daily/5day/215854?apikey=WebHzrX0S4ZGR1xqMmgg9s1cMgcjL6CE"



  return (
    <div className="App">
      <Router>
        <Header/>
        <hr/>
          {/* <div>{citys[0].LocalizedName}</div>
      <div>{currentcondition.WeatherText}</div>
      <div>{currentcondition.Temperature.Metric.Value}C</div> */}
         <Routes>
            <Route path='/' element ={<HomePage  saveFavoriteCity = {saveFavoriteCity} />}/>
            <Route path='/favorite' element={<FavoritePage/>} />
         </Routes>
      </Router>

    </div>
  );
}

export default App;
