import React, { useState } from 'react';
import Manu from './Manu'
import BoardFlyts from './BoardFlyts'

export default function ControlPanel(props) {

    const [table, setTable] = useState("allflyts")
    const changeTable = (t) => {setTable(t)}


    const flyts = [
      {flytN:123, company:"abc",passengerN:100},
      {flytN:345, company:"def",passengerN:200},
      {flytN:567, company:"ghi",passengerN:300},
      {flytN:789, company:"jkl",passengerN:400}
    ]
      
    const swichTableView = () => {
        switch(table) {
          case "allflyts":
            return <BoardFlyts flyts={flyts}  /> 
    
          case "sortflyts":
            return <BoardFlyts flyts={flyts} /> 
    
          case "addflyts":
        
            return <BoardFlyts  flyts={props.flyts} /> 
    
            case "deleteflyts":
              return <BoardFlyts changepage={changeTable}  flyts={props.flyts} />
        }
      }
    
  return (
    <div>
        <Manu/>
        {swichTableView()}   
         </div>
  )
}
