import React from "react";
import './style/App.css';
import { useState } from "react";




const carImages = ['images/tesla-white.jpg', 'images/tesla-black.jpg', 'images/tesla-red.jpg']

function App() {
    const [pos,setPos]=useState(carImages)
    const changePos = ()=>{
      if(pos ==carImages.length -1){
        setPos(0)
      }else{
        setPos(pos+1)
      }

    }

  return (
    <div className="">
      <img className="carBox" src={carImages[pos]}  onClick={changePos} alt="" />      

    </div>
  );
}

export default App;
