import { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Home from './Component/Home';
import './App.css';
import Trainer from './Component/Trainer';

const tUser = { id: '1234', fName: 'el', gender: 'test', trainingAmount: 5 ,trainingYears: 4}

function App() {

  const [user, setUser] = useState(tUser)

  const addUser = (user) => setUser(user)



  return (
    <div className="App">
     <Router>
      <Routes>
        <Route path='/' element={<Home addUser={addUser} />}/>
        <Route path={'/trainer/' + user.fName} element={<Trainer user={user}/>}/>
      </Routes>
     </Router>
    </div>
  );
}

export default App;
