import React, {  useEffect, useState } from 'react';
import Table from './Table';
export default function TasksPage() {
  const myTasks = [
    { tName: "New Homepage", tDescription: "Create new homepage for the company XYZ", tAssigee: "Dani Litto" },
    { tName: "Repair Door", tDescription: "The front door is loose", tAssigee: "Joe Gibon" },
    { tName: "Buy Board", tDescription: "Need board for my lesons", tAssigee: "Vera Loo" }
]
  const [list, setList] = useState([])

  const getAllTasks =() => {
    fetch('/tasks')
    .then(res => res.json())
    .then(data =>setList(data))
  }

  useEffect(getAllTasks,[])
  return (
    <div>{list.map(item =>{
      return(
        <div> 
          {/* <Table options={item.tName} />
          <Table options={item.tDescription} /> */}
          <Table options={myTasks} onChangeFn={getAllTasks}/>

         {/* <h3>{item.tName}</h3>
         <h3>{item.tDescription}</h3>
         <h3>{item.tAssigee}</h3> */}
        </div>
      )
     })}</div>
  )
}
