import React, {useState} from 'react'
import { useNavigate } from 'react-router-dom';

export default function CancelPage(props) {

    // const [validPass, setValidPass] = useState(false)
    const [confirmPassword, setConfirmPassword] = useState("")
    const [password, setPassword] = useState("")
    const [counter, setCounter] = useState(0)
    const navigate = useNavigate()

// const isPassValid = (p) => p.length >= 8  ? true : false

    const isPasswordValid = ()=> {
        if(password == "" || confirmPassword == "") {
            alert("empty fileds")
            setCounter(counter+1)
        } else if(confirmPassword != password){
            alert("not equal")
            setCounter(counter+1)
        } else if(password != props.user.password){
            alert("not valid")
            setCounter(counter+1)
        } else {
            
            props.changePage("helpbtn")
        }
        
        // if(counter >= 3) {
        //     setValidPass(true)
        // }
       
        // הגבלת מספר לחיצות בעזרת אינדקס
        // if password && confirm not empty!!
        // if password and confirm are equal!!
        // if password local and user password are equal 
        // if one of them is failed count it (3 tries)
        // if (props.user.password ==)
    }

    // לפי הסדר הבא:
    // תנאי ראשון: האם שדה הסיסמה ושדה האישור סיסמה רייקים
    // תנאי שני: האם שדה הסימה ושדה אישור סיסמה שווים
    // תנאי שלישי: האם השדות שוים לסיסמה של היוזר מעמוד הרישום
    // תנאי רביעי: האם נעשו 3 נסיונות אם כן חסימה 
  return (
    <div>
        <input type="text" onChange={(e)=>setPassword(e.target.value)} placeholder= {"password"} />
        <input type="text" onChange={(e)=>setConfirmPassword(e.target.value)} placeholder= {"confirm password"}/> <br />
        {/* <button disabled={validPass} onClick={isPasswordValid}>Cancel1</button> */}
        <button disabled={counter < 3 ? false : true} onClick={isPasswordValid}>Cancel2</button>
    </div>
  )
}
