import React, {  useState } from 'react';

export default function AddWilling() {
const [title, setTitle] = useState("")
const [location, setLocation] = useState("")
const [tiWilling , setTiWillin] = useState("")


// 1 .כותרת להתנדבות – אינפוט טקסט אשר מכיל עד 20 תווים.
// מטרתו לתאר את ההתנדבות
// 2 .מיקום ההתנדבות – שם של עיר, חייב לכלול אותיות באנגלית בלבד
// ללא מספרים רווחים או כל סימן
// 3 .תיאור ההתנדבות – מכיל עד 200 תווים, תיאור של ההתנדבות
// 4 .כפתור יצירת התנדבות חדשה
// מוודא את תקינות השדות, במידה והכל תקין מחזיר את הלקוח לעמוד הראשי,
// במידה ואחד הערכים לא תקין שולח ALERT עם התקלה.
const addBtn = () => {
    if(title.length > 20) {
        console.log("bad")
        alert("no more 20 charts")
    } else if(location != ('a')-('z')) {
        console.log("bad")

    }else {
        console.log("good")

    }

}




  return (
    <div>
        <input type="text" onChange={(e)=>{setTitle(e.target.value)}} placeholder={"title"} /> <br />
        <input type="text" onChange={(e)=>{setLocation(e.target.value)}} placeholder={"location"} /> <br />
        <input type="text" onChange={(e)=>{setTiWillin(e.target.value)}} placeholder={"title-willing"} /> <br />
        <button className='largeBtn' onClick={addBtn}>add</button>
    </div>
  )
}
