import React, {useState} from 'react'

//    props.car
// { 
  //  model:'Tesla Model 3',
  //  price: 50000, 
  //  description:'Exterior lights, Autopilot Camera, Front door handle', 
  //  imagesPath: ['images/tesla-white.jpg', 'images/tesla-black.jpg', 'images/tesla-red.jpg']
// },

export default function Car(props) {

  const [pos, setPos] = useState(0)

  const changePos = () => {
    if(pos == props.car.imagesPath.length - 1) {
      setPos(0)
    } else {
      setPos(pos+1)
    }
  }
  

  return (
    <div >
        <h4>{props.car.model}</h4>
        <div>Price: {props.car.price}$</div>
        <div>Description: {props.car.description}</div>
        <img className='carBox' src={props.car.imagesPath[pos]} onClick={changePos} alt={`modleCar${props.car.model}`}/>
    </div>
  )
}
