import {BrowserRouter as Router, Routes , Route, Navigate} from 'react-router-dom';
import './App.css';


export default function App() {

  const [currentConditions, setCurrentConditions] = useState({})
  const [citis, setCitis] = useState([])
  const [cityName, setCityName] = useState("Tel aviv")
  const [cityKey, setCityKey] = useState('')

  const API_KEY = 'WebHzrX0S4ZGR1xqMmgg9s1cMgcjL6CE'

// tel aviv key - 215854

// autocomplete
// "http://dataservice.accuweather.com/locations/v1/cities/autocomplete?apikey=WebHzrX0S4ZGR1xqMmgg9s1cMgcjL6CE&q=tel"

// currentconditions
// "http://dataservice.accuweather.com/currentconditions/v1/215854?apikey=WebHzrX0S4ZGR1xqMmgg9s1cMgcjL6CE"

// 5 Days
// "http://dataservice.accuweather.com/forecasts/v1/daily/5day/215854?apikey=WebHzrX0S4ZGR1xqMmgg9s1cMgcjL6CE"

  const getCurrentCondtion = () => {
    fetch(`http://dataservice.accuweather.com/currentconditions/v1/215854?apikey=${API_KEY}`)
      .then((res)=>res.json())
      .then((data)=>setCurrentConditions(data))
  }

  const getCityKey = () => {
    fetch(`http://dataservice.accuweather.com/locations/v1/cities/autocomplete?apikey=${API_KEY}&q=london`)
    .then(res=>res.json())
    .then(data=>{
      setCityKey(data[0].key)
      setCitis(data)
    })
  }


  useEffect(()=>{
    console.log('C:\Users\Eli Abramov\Desktop\nodejs\praReact\src\autocomplete.json');
    fetch('/autocomplete.json', {
      headers : { 
        'Content-Type': 'application/json',
        'Accept': 'application/json'
       }
    })
    .then(res=>{
      console.log(res);
      res.json()
    })
    .then(data=>{
      console.log(data);
      setCitis(data[0])
    })
  }, [])




  return (
    <div className="App">
      <h1>Weatehr app</h1>
    </div>
  );
}
